import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminDashboard() {
  const [data, setData] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone_number, setPhone] = useState('');
  const [winnerInfo, setWinnerInfo] = useState({
    id: '',
    name: '',
    email: ''
  });

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please login as admin');
        window.location.href = '/admin/login';
        return;
      }

      try {
        const res = await axios.get('http://localhost:3001/admin/data', {
          headers: { Authorization: token }
        });
        setData(res.data);
      } catch (err) {
        alert('Access denied');
        window.location.href = '/admin/login';
      }
    };
    fetchData();
  }, []);

  // Fetch participants from API
  useEffect(() => {
    axios.get('http://localhost:3001/database/participants')
      .then(response => setParticipants(response.data))
      .catch(error => console.error('Error fetching participants:', error));
  }, []);

  // Add new participant
  const addParticipant = () => {
    axios.post('http://localhost:3001/database/participants', { name, email, phone_number })
      .then(response => {
        // 從回應中提取 insertId 並構建新的參與者資料
        const newParticipant = {
          id: response.data.result.insertId, // 從 result 中提取 insertId
          name,
          email,
          phone_number
        };
        // 更新 participants 陣列
        setParticipants([...participants, newParticipant]);
        // 清空輸入欄位
        setName('');
        setEmail('');
        setPhone('');
      })
      .catch(error => console.error('Error adding participant:', error));
  };

  // Draw a random winner
  const drawWinner = async () => {
    try {
      const response = await fetch("http://localhost:3001/database/random-winner");
      const data = await response.json();
      setWinnerInfo(data); // Update winner details
    } catch (error) {
      console.error("Error fetching winner:", error);
    }
  };

  // const idtoname = () => {
  //   axios.get('http://localhost:3001/database/idtoname')
  //   .then(response => setParticipants(response.data))

  // }

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <div>
        <h2>管理參與者</h2>
        <div>
          <input
            type="text"
            placeholder="姓名"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="text"
            placeholder="電話"
            value={phone_number}
            onChange={(e) => setPhone(e.target.value)}
          />
          <button onClick={addParticipant}>新增參與者</button>
        </div>
        <ul>
          {participants.map((participant, index) => (
            <li key={index}>
               ({participant.id}) {participant.name}, {participant.email}, {participant.phone_number}
            </li>
          ))}
        </ul>
      </div>
      <div style={{ marginTop: "50px" }}>
        <h2>抽取中獎者</h2>
        <button onClick={drawWinner}>抽取參與者</button>
        {winner && (
          <div style={{ marginTop: "20px" }}>
            <h3>中獎者資訊</h3>
            <p><strong>ID:</strong> {winnerInfo.id}</p>
            <p><strong>姓名:</strong> {winnerInfo.name}</p>
            <p><strong>Email:</strong> {winnerInfo.email}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
