import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminDashboard() {
  // const [data, setData] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone_number, setPhone] = useState('');
  const [winnerInfo, setWinnerInfo] = useState({
    id: '',
    name: '',
    email: '',
    phone_number: ''
  });
  const [updateId, setUpdateId] = useState('');
  const [message, setMessage] = useState(''); //用於回報刪除、修改參與者
  const [query, setQuery] = useState("");     //用於搜索參與者
  // const [results, setResults] = useState([]); //用於搜索參與者

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please login as admin');
        window.location.href = '/admin/login';
        return;
      }

      try {
        // const res = await axios.get('http://localhost:3001/admin/data', {
        //   headers: { Authorization: token }
        // });
        // setData(res.data);
      } catch (err) {
        alert('Access denied');
        window.location.href = '/admin/login';
      }
    };
    fetchData();
  }, []);

  // Fetch participants from API
  useEffect(() => {
    axios.get('http://localhost:3001/database/allparticipants')
      .then(response => setParticipants(response.data))
      .catch(error => console.error('Error fetching participants:', error));
  }, []);

  // Add new participant
  const addParticipant = () => {
    axios.post('http://localhost:3001/database/addparticipants', { name, email, phone_number })
      .then(response => {
        // 從回應中提取 insertId 並構建新的參與者資料
        const newParticipant = {
          id: response.data.result.insertId, // 從 result 中提取 insertId
          name,
          email,
          phone_number
        };
        // 更新 participants 陣列
        setParticipants([...participants, newParticipant]);
        // 清空輸入欄位
        setName('');
        setEmail('');
        setPhone('');
      })
      .catch(error => console.error('Error adding participant:', error));
  };

  // Delete participant
  const delParticipant = async () => {
    try {
        const response = await axios.delete('http://localhost:3001/database/delparticipants', {
            data: { name },
        });
        const updatedParticipants = participants.filter(participant => participant.name !== name);
        setParticipants(updatedParticipants); // 從狀態中移除已刪除的參與者
        setMessage(response.data); // 顯示成功訊息
        setName(''); // 清空 name
    } catch (error) {
        setMessage('Error deleting participant');
    }
  };


  // Update participant
  const updateParticipant = () => {
    axios.put('http://localhost:3001/database/updateparticipant', {
      id: updateId,
      name,
      phone_number,
      email
    })
      .then(response => {
        setMessage(response.data.message);
        setParticipants(participants.map(participant =>
          participant.id === parseInt(updateId, 10)
            ? { ...participant, name, phone_number, email }
            : participant
        ));
        setUpdateId('');
        setName('');
        setPhone('');
        setEmail('');
      })
      .catch(error => {
        console.error('Error updating participant:', error);
        setMessage('Error updating participant.');
      });
  };

  const SearchParticipant = async (event) => {
    const value = event.target.value;
    setQuery(value);

    try {
      const url = value.trim() === "" 
          ? `http://localhost:3001/database/allparticipants` // 修改為請求顯示所有內容的端點
          : `http://localhost:3001/database/search?query=${value}`;
      
      const response = await fetch(url);
      const data = await response.json();
      setParticipants(data);
    } catch (error) {
        console.error("Error fetching search results:", error);
    }
  };

  // Draw a random winner
  const drawWinner = async () => {
    try {
      const response = await fetch("http://localhost:3001/database/random-winner");
      const data = await response.json();
      setWinnerInfo(data[0]); // Update winner details
    } catch (error) {
      console.error("Error fetching winner:", error);
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <div>
        <h2>管理參與者</h2>
        <div>
          <h3>新增參與著</h3>
          <input
            type="text"
            placeholder="姓名"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="text"
            placeholder="電話"
            value={phone_number}
            onChange={(e) => setPhone(e.target.value)}
          />
          <button onClick={addParticipant}>新增參與者</button>
          <h3>刪除參與著</h3>
          <input
            type="text"
            placeholder="姓名"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <button onClick={delParticipant}>Delete</button>
          <h3>更新參與者</h3>
          <input
            type="text"
            placeholder="ID"
            value={updateId}
            onChange={(e) => setUpdateId(e.target.value)}
          />
          <input
            type="text"
            placeholder="姓名"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="text"
            placeholder="電話"
            value={phone_number}
            onChange={(e) => setPhone(e.target.value)}
          />
          <button onClick={updateParticipant}>更新參與者</button>
          {message && <p>{message}</p>}
          <input
            type="text"
            value={query}
            onChange={SearchParticipant}
            placeholder="Type to search..."
            style={{ width: "300px", padding: "10px" }}
          />
        </div>
        <table border="1">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Phone Number</th>
          </tr>
        </thead>
        <tbody>
          {participants.map((participant) => (
            <tr key={participant.id}>
              <td>{participant.id}</td>
              <td>{participant.name}</td>
              <td>{participant.email}</td>
              <td>{participant.phone_number}</td>
            </tr>
          ))}
        </tbody>
      </table>
      </div>
      <div style={{ marginTop: "50px" }}>
        <h2>抽取中獎者</h2>
        <button onClick={drawWinner}>抽取參與者</button>
        {winnerInfo && (
          <div style={{ marginTop: "20px" }}>
            <h3>中獎者資訊</h3>
            <p><strong>ID:</strong> {winnerInfo.id}</p>
            <p><strong>name:</strong> {winnerInfo.name}</p>
            <p><strong>Email:</strong> {winnerInfo.email}</p>
            <p><strong>Phone:</strong> {winnerInfo.phone_number}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
