import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [participants, setParticipants] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  useEffect(() => {
    axios.get('http://localhost:3001/participants')
      .then(response => setParticipants(response.data))
      .catch(error => console.error('Error fetching participants:', error));
  }, []);

  const addParticipant = () => {
    axios.post('http://localhost:3001/participants', { name, email })
      .then(response => {
        setParticipants([...participants, { name, email }]);
        setName('');
        setEmail('');
      })
      .catch(error => console.error('Error adding participant:', error));
  };

  return (
    <div className="App">
      <h1>Lottery Participants</h1>
      <ul>
        {participants.map((participant, index) => (
          <li key={index}>{participant.name} ({participant.email})</li>
        ))}
      </ul>

      <div>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button onClick={addParticipant}>Add Participant</button>
      </div>
    </div>
  );
}

export default App;
