import React, { useEffect, useState } from 'react';
import axios from 'axios';

function AdminDashboard() {
  const [participants, setParticipants] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone_number, setPhone] = useState('');
  const [query, setQuery] = useState("");
  const [editingCell, setEditingCell] = useState(null);
  const [winnerInfo, setWinnerInfo] = useState(null);

  // 驗證是否為管理員
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login as admin');
      window.location.href = '/admin/login';
    }
  }, []);

  // 獲取參與者資料
  useEffect(() => {
    fetchParticipants();
  }, []);

  const fetchParticipants = async () => {
    try {
      const response = await axios.get('http://localhost:3001/database/allparticipants');
      setParticipants(response.data);
    } catch (error) {
      console.error('Error fetching participants:', error);
    }
  };

  // 新增參與者
  const addParticipant = async () => {
    try {
      const response = await axios.post('http://localhost:3001/database/addparticipants', { name, email, phone_number });
      const newParticipant = {
        id: response.data.result.insertId,
        name,
        email,
        phone_number,
      };
      setParticipants([...participants, newParticipant]);
      setName('');
      setEmail('');
      setPhone('');
    } catch (error) {
      console.error('Error adding participant:', error);
    }
  };

  // 搜尋參與者
  const searchParticipant = async (event) => {
    const value = event.target.value;
    setQuery(value);

    try {
      const response = await axios.get(value.trim() ? `http://localhost:3001/database/search?query=${value}` : 'http://localhost:3001/database/allparticipants');
      setParticipants(response.data);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  // 編輯參與者
  const handleInputChange = (event, participantId, field) => {
    const value = event.target.value;
    setParticipants((prev) =>
      prev.map((p) => (p.id === participantId ? { ...p, [field]: value } : p))
    );
  };

  const handleBlur = async (participantId, field, value) => {
    try {
      await axios.put('http://localhost:3001/database/updateparticipant', { id: participantId, [field]: value });
      setEditingCell(null);
    } catch (error) {
      console.error('Error updating participant:', error);
    }
  };

  // 抽取中獎者
  const drawWinner = async () => {
    try {
      const response = await axios.get('http://localhost:3001/database/random-winner');
      setWinnerInfo(response.data[0]);
    } catch (error) {
      console.error('Error fetching winner:', error);
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <div>
        <h2>管理參與者</h2>
        <div>
          <h3>新增參與者</h3>
          <input type="text" placeholder="姓名" value={name} onChange={(e) => setName(e.target.value)} />
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
          <input type="text" placeholder="電話" value={phone_number} onChange={(e) => setPhone(e.target.value)} />
          <button onClick={addParticipant}>新增參與者</button>
          <h3>搜尋參與者</h3>
          <input type="text" value={query} onChange={searchParticipant} placeholder="Type to search..." />
        </div>
        <div>
          <table border="1">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Email</th>
                <th>Phone Number</th>
              </tr>
            </thead>
            <tbody>
              {participants.map((participant) => (
                <tr key={participant.id}>
                  <td>{participant.id}</td>
                  <td onDoubleClick={() => setEditingCell({ id: participant.id, field: 'name' })}>
                    {editingCell?.id === participant.id && editingCell?.field === 'name' ? (
                      <input
                        type="text"
                        value={participant.name}
                        onChange={(e) => handleInputChange(e, participant.id, 'name')}
                        onBlur={(e) => handleBlur(participant.id, 'name', e.target.value)}
                        autoFocus
                      />
                    ) : (
                      participant.name
                    )}
                  </td>
                  <td onDoubleClick={() => setEditingCell({ id: participant.id, field: 'email' })}>
                    {editingCell?.id === participant.id && editingCell?.field === 'email' ? (
                      <input
                        type="text"
                        value={participant.email}
                        onChange={(e) => handleInputChange(e, participant.id, 'email')}
                        onBlur={(e) => handleBlur(participant.id, 'email', e.target.value)}
                        autoFocus
                      />
                    ) : (
                      participant.email
                    )}
                  </td>
                  <td onDoubleClick={() => setEditingCell({ id: participant.id, field: 'phone_number' })}>
                    {editingCell?.id === participant.id && editingCell?.field === 'phone_number' ? (
                      <input
                        type="text"
                        value={participant.phone_number}
                        onChange={(e) => handleInputChange(e, participant.id, 'phone_number')}
                        onBlur={(e) => handleBlur(participant.id, 'phone_number', e.target.value)}
                        autoFocus
                      />
                    ) : (
                      participant.phone_number
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      <div style={{ marginTop: '50px' }}>
        <h2>抽取中獎者</h2>
        <button onClick={drawWinner}>抽取參與者</button>
        {winnerInfo && (
          <div style={{ marginTop: '20px' }}>
            <h3>中獎者資訊</h3>
            <p><strong>ID:</strong> {winnerInfo.id}</p>
            <p><strong>name:</strong> {winnerInfo.name}</p>
            <p><strong>Email:</strong> {winnerInfo.email}</p>
            <p><strong>Phone:</strong> {winnerInfo.phone_number}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
