  //   } catch (error) {
  //     setMessage('Error deleting participant');
  //   }
  // };

  // Update participant
  // const updateParticipant = () => {
  //   axios.put('http://localhost:3001/database/updateparticipant', {
  //     id: updateId,
  //     name,
  //     phone_number,
  //     email
  //   })
  //     .then(response => {
  //       setMessage(response.data.message);
  //       setParticipants(participants.map(participant =>
  //         participant.id === parseInt(updateId, 10)
  //           ? { ...participant, name, phone_number, email }
  //           : participant
  //       ));
  //       setUpdateId('');
  //       setName('');
  //       setPhone('');
  //       setEmail('');
  //     })
  //     .catch(error => {
  //       console.error('Error updating participant:', error);
  //       setMessage('Error updating participant.');
  //     });
  // };

  // Draw a random winner
  const drawWinner = async () => {
    try {
      const response = await fetch("http://localhost:3001/database/random-winner");
      const data = await response.json();
      setWinnerInfo(data[0]); // Update winner details
    } catch (error) {
      console.error("Error fetching winner:", error);
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <div>
        <h2>管理參與者</h2>
        <div>
          <h3>新增參與著</h3>
          <input
            type="text"
            placeholder="姓名"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="text"
            placeholder="電話"
            value={phone_number}
            onChange={(e) => setPhone(e.target.value)}
          />
          <button onClick={addParticipant}>新增參與者</button>
          {/* <h3>刪除參與著</h3>
          <input
            type="text"
            placeholder="Enter participant name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <button onClick={delParticipant}>Delete</button>
          <h3>更新參與者</h3>
          <input
            type="text"
            placeholder="ID"
            value={updateId}
            onChange={(e) => setUpdateId(e.target.value)}
          />
          <input
            type="text"
            placeholder="姓名"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="text"
            placeholder="電話"
