            onChange={(e) => setPhone(e.target.value)}
          />
          <button onClick={updateParticipant}>更新參與者</button>
          {message && <p>{message}</p>} */}
        </div>
        <ul>
          {participants.map((participant, index) => (
            <li key={index}>
               ({participant.id}) {participant.name}, {participant.email}, {participant.phone_number}
            </li>
          ))}
        </ul>
      </div>
      <div style={{ marginTop: "50px" }}>
        <h2>抽取中獎者</h2>
        <button onClick={drawWinner}>抽取參與者</button>
        {winnerInfo && (
          <div style={{ marginTop: "20px" }}>
            <h3>中獎者資訊</h3>
            <p><strong>ID:</strong> {winnerInfo.id}</p>
            <p><strong>name:</strong> {winnerInfo.name}</p>
            <p><strong>Email:</strong> {winnerInfo.email}</p>
            <p><strong>Phone:</strong> {winnerInfo.phone_number}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
